mapper.select:
SELECT *
FROM `member`
WHERE loginId = #{loginId}
AND
loginPw = #{loginPw}

mapper.update:
UPDATE `member`
updateDate = NOW(), loginPw = #{loginPw},
WHERE id = #{id}

